export default {
  name: "SportBall",
  data: function data() {
    return {
      isShow: false
    };
  },
  methods: {
    run: function run(target) {
      this.isShow = true;
    },
    beforeEnter: function beforeEnter(el) {
      el.style.display = ''; // 清空display

      el.style.webkitTransform = "translate3d(0, 0, 0)";
      el.style.transform = "translate3d(0, 0, 0)"; // 处理内层动画

      var inner = el.getElementsByClassName('inner-ball')[0];
      inner.style.webkitTransform = "translate3d(0, 0, 0)";
      inner.style.transform = "translate3d(0, 0, 0)";
      el.style.opacity = 1.0;
    },
    enter: function enter(el, done) {
      var rf = el.offsetHeight; //触发重绘html
      // 让动画效果异步执行,提高性能

      el.style.webkitTransform = 'translate3d(0, -100px, 0)';
      el.style.transform = 'translate3d(0, -100px, 0)'; // 处理内层动画

      var inner = el.getElementsByClassName('inner-ball')[0];
      inner.style.webkitTransform = 'translate3d(100px, 0, 0)';
      inner.style.transform = 'translate3d(100px, 0, 0)';
      el.style.opacity = 0.3;
      el.addEventListener('transitionend', done);
    },
    leave: function leave(el) {
      this.isShow = false;
    }
  }
};
