import Vue from 'vue'
import App from './App.vue'
import router from './router'

Vue.config.productionTip = false

new Vue({
  render: h => h(App),
  router
}).$mount('#app')


// Promise的使用
// resolve/reject都是函数
const func = function (resolve, reject) {
  resolve(111111)
  // reject()
}

const p = new Promise(func)

p.then((res) => {
  console.log('----', res);
}).catch(() => {
  console.log('+++++');
})
