  import NavBar from 'components/common/navbar/NavBar'
  import HomeSwiper from './childComps/HomeSwiper'
  import RecommendView from './childComps/RecommendView'
  import FeatureView from './childComps/FeatureView'
  import TabControl from 'components/content/tabControl/TabControl'
  import GoodsList from '../../components/content/goods/GoodsList'
  import Scroll from 'components/common/scroll/Scroll'

  import {getMultiData, getProductData} from "network/home";
  import {TOP_DISTANCE, POP, NEW, SELL} from "common/const";
  import {backTopMixin} from "common/mixin";

  export default {
    name: "Home",
    components: {
      NavBar,
      HomeSwiper,
      RecommendView,
      FeatureView,
      TabControl,
      GoodsList,
      Scroll
    },
    mixins: [backTopMixin],
    data() {
      return {
        banners: [],
        recommends: [],
        goods: {
          'pop': {page: 1, list: []},
          'new': {page: 1, list: []},
          'sell': {page: 1, list: []},
        },
        currentType: POP,
        showTabControl: false,
        tabOffsetTop: 0,
        saveY: 0
      }
    },
    computed: {
      showGoodsList() {
        return this.goods[this.currentType].list
      }
    },
    created() {
      // 1.请求轮播等数据
      this._getMultiData()

      // 2.请求商品数据
      this._getProductData(POP)
      this._getProductData(NEW)
      this._getProductData(SELL)

      // 3.监听一些事件
      this.$bus.$on('imgLoad', () => {
      	this.$refs.scroll.refresh()
      })
    },
    activated() {
      this.$refs.scroll.scrollTo(0, this.saveY, 0)
      this.$refs.scroll.refresh()
    },
    deactivated() {
	    this.saveY = this.$refs.scroll.scrollY
    },
    methods: {
      /**
       * 网络请求
       */
      _getMultiData() {
        getMultiData().then(res => {
          this.banners = res.data.banner.list
          this.recommends = res.data.recommend.list
        })
      },
      _getProductData(type) {
        // 获取页码
        const page = this.goods[type].page
        getProductData(type, page).then(res => {
          const newList = res.data.list
          this.goods[type].list.push(...newList)
          this.goods[type].page += 1

          // 完成加载更多数据
          this.$refs.scroll.finishedPullUp()
        })
      },
      /**
       * 事件监听
       */
      tabClick(index) {
        switch (index) {
          case 0:
            this.currentType = POP
            break
          case 1:
            this.currentType = NEW
            break
          case 2:
            this.currentType = SELL
            break
        }

        this.$refs.contentTab.currentIndex = index
        this.$refs.topTab.currentIndex = index
      },
      loadMore() {
        this._getProductData(this.currentType)
      },
      contentScroll(position) {
        this.showBackTop = position.y <= -TOP_DISTANCE
        this.showTabControl = position.y <= -this.tabOffsetTop
      },
	    swiperLoaded() {
		    this.tabOffsetTop = this.$refs.contentTab.$el.offsetTop;
	    }
    }
  }
